package ejercicios_java_1;

public class Ejercicio_1{
	public static void main(String[] args) {
		// TODO Auto-generated method stub
		int numero = 5;
		System.out.println("El numero es " + numero);
		double num_decimal = 5d;
		System.out.print("El numero es " + num_decimal);
		boolean verda = true;
		System.out.print("Es " + verda);
		char letra = 'K';
		System.out.print("La letra es " + letra);
		String palabra = "Hola";
		System.out.print("La palabra es " + palabra);
	}
}
